import convertDuration from "../../../utils/convertDuration";

const MoviesCard = ({ movie }) => {

  return (
    <li className="movie">
      {/* <Routes>
        <Route path="/movies" render={() => {
          return (
            <a href={movie.trailerLink} target="blank">
              <img
                className="movie__image"
                src={movie.image}
