import MoviesCardList from "./MoviesCardList/MoviesCardList";
import SearchForm from "./SearchForm/SearchForm";
import Preloader from "./Preloader/Preloader";

const Movies = ({ moviesList, onProcessSearchRequest, foundMoviesList, savedMoviesList, preloaderState, onChangeCheckboxState, checkboxState }) => {
  return (
    <>
      <main className="movies">
        <SearchForm onSearch={onProcessSearchRequest} onTickCheckbox={onChangeCheckboxState} checkboxState={checkboxState}/>
        {preloaderState ? (
          <Preloader />
        ) : (
          <MoviesCardList moviesList={foundMoviesList} savedMoviesList={savedMoviesList}/>
        )}
      </main>
    </>
  );
}

export default Movies;
