import MoviesCard from "../MoviesCard/MoviesCard";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const MoviesCardList = ({ moviesList, savedMoviesList, onSaveMovie, onDeleteMovie }) => {
  const [browserWidth, setBrowserWidth] = useState(1280);
  const [maxCardsToRender, setMaxCardsToRender] = useState(12);

  const [moviesToRender, setMoviesToRender] = useState([]);

  const location = useLocation();

  const setDefaultMoviesVisible = (count) => {
    setMaxCardsToRender(count);
    let movies = [];
    for (let i = 0; i < count; ++i) {
      movies.push(moviesList[i]);
    }
    setMoviesToRender(movies);
  }

  const setMoviesVisible = () => {
    let movies = [];
    for (let i = 0; i < maxCardsToRender; ++i) {
      movies.push(moviesList[i]);
    }
    setMoviesToRender(movies);
  }

  useEffect(() => {
    if (browserWidth < 480) {
      setMoviesVisible(5);
    } else if (browserWidth < 768) {
      setMoviesVisible(8);
    } else if (browserWidth < 1280) {
      setMoviesVisible(9);
    } else {
      setMoviesVisible(12);
    }
    if (location.pathname === "/saved-movies") {
      setMaxCardsToRender(120);
    }
  }, [browserWidth, location]);


  return (
    <section className="movies-list__wrapper">
      <ul className="movies-list">
        {moviesList.map((movie) => (
          <MoviesCard
            key={movie.id}
            movie={movie}
          />
        ))}
      </ul>
      <button
        className="movies-list__button"
        type="button"
      >
        Ещё
      </button>
      {/* <div className="movies-list__button-replacement"></div> */}
    </section>
  );
}

export default MoviesCardList;
