import "./App.css";

import { Route, Routes } from "react-router-dom";
import { CurrentUserContext } from "../../contexts/CurrentUserContext.js";
import React, { useState } from "react";

import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import Main from "../Main/Main";
import Movies from "../Movies/Movies";
import SavedMovies from "../SavedMovies/SavedMovies";
import Page404 from "../Page404/Page404";

import Register from "../Register/Register";
import Login from "../Login/Login";
import Profile from "../Profile/Profile";

const App = () => {
  const [currentUser, setCurrentUser] = useState({
    email: "",
    name: "",
    _id: "",
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isBurgerOpened, setIsBurgerOpened] = useState(false);

  const onBurgerClick = () => {
    setIsBurgerOpened(!isBurgerOpened);
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="app">
        <Routes>
          <Route path="/" element={<Header isLoggedIn={isLoggedIn} isBurgerOpened={isBurgerOpened} onBurgerClick={onBurgerClick}/>}>
            <Route index element={
                <Main />
            } />
            <Route path="/profile" element={<Profile/>} />
            <Route path="/movies" element={
              <>
                <Movies />
                <Footer />
              </>
            } />
            <Route path="/saved-movies" element={
              <>
                <SavedMovies />
                <Footer />
              </>
            } />
          </Route>

          {!isLoggedIn && (
            <>
              <Route path="/signin" element={<Login onLoginClick={setIsLoggedIn}/>} />
              <Route path="/signup" element={<Register onRegisterClick={setIsLoggedIn}/>} />
            </>
          )}

          <Route path="*" element={<Page404/>} />
        </Routes>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
