import "./App.css";

import { Redirect, Route, Switch } from "react-router-dom";

import ProtectedRoute from "../ProtectedRoute/ProtectedRoute";

import { CurrentUserContext } from "../../contexts/CurrentUserContext";

import Header from "../Header/Header";
import Footer from "../Footer/Footer";

import Main from "../Main/Main";

import Movies from "../Movies/Movies";
import SavedMovies from "../SavedMovies/SavedMovies";

import PageNotFound from "../PageNotFound/PageNotFound";

import InfoTooltip from "../InfoTooltip/InfoTooltip";

import Register from "../Register/Register";
import Login from "../Login/Login";
import Profile from "../Profile/Profile";

import * as auth from "../../utils/auth";
import mainApi from "../../utils/MainApi";
import moviesApi from "../../utils/MoviesApi";

const App = ({}) => {

  return (
    <div className="App">
      <CurrentUserContext.Provider value={currentUser}>
        <Route exact path={headerEndpoints}>
          <Header
            onBurgerClick={toggleBurgerMenuClick}
            isOpen={isBurgerMenuOpen}
            loggedIn={loggedIn}
          />
        </Route>
        <Switch>
          <Route exact path="/">
            <Main />
          </Route>
          <ProtectedRoute
            path="/movies"
            component={Movies}
            onSearch={handleSearchMovie}
            foundMovies={foundMovies}
            savedMovies={savedMovies}
            onSaveMovie={handleSaveMovie}
            onDeleteMovie={handleDeleteMovie}
            disabled={checkboxStatus}
            onSubmitCheckbox={handleSubmitCheckbox}
            disabledCheckbox={disabledCheckbox}
            preloaderStatus={preloaderStatus}
          />
          <ProtectedRoute
            path="/saved-movies"
            component={SavedMovies}
            onSearch={handleSearchSavedMovie}
            savedMovies={savedMovies}
            onSubmitCheckbox={handleSavedMoviesSubmitCheckbox}
            disabled={checkboxStatus}
            onDeleteMovie={handleDeleteMovie}
            onSaveMovie={handleSaveMovie}
            disabledCheckboxSaved={disabledCheckboxSaved}
            preloaderStatus={preloaderStatus}
          />
          <Route exact path="/signup">
            {loggedIn ? (
              <Redirect to="/" />
            ) : (
              <Register onRegister={handleRegister} isLoading={isLoading} />
            )}
          </Route>
          <Route exact path="/signin">
            {loggedIn ? (
              <Redirect to="/" />
            ) : (
              <Login
                onLogin={handleLogin}
                history={history}
                isLoading={isLoading}
              />
            )}
          </Route>
          <ProtectedRoute
            path="/profile"
            component={Profile}
            onSignout={handleSignout}
            loggedIn={loggedIn}
            onUpdateUser={handleUpdateUser}
            isLoading={isLoading}
          />
          <Route path="*" component={PageNotFound} history={history} />
        </Switch>
        <Route exact path={footerEndpoints} component={Footer} />
        <InfoTooltip
          isOpen={isTooltipPopupOpen}
          onClose={closePopup}
          success={success}
          text={popupText}
        />
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
