import "./App.css";

import { Redirect, Route, Switch } from "react-router-dom";

import ProtectedRoute from "../ProtectedRoute/ProtectedRoute";

import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import Main from "../Main/Main";
import Movies from "../Movies/Movies";
import SavedMovies from "../SavedMovies/SavedMovies";
import Page404 from "../Page404/Page404";

import Register from "../Register/Register";
import Login from "../Login/Login";
import Profile from "../Profile/Profile";

const App = ({}) => {

  return (
    <div className="App">
      <Header/>
      <Switch>
        <Route exact path="/">
          <Main />
        </Route>
        <ProtectedRoute
          path="/movies"
          component={Movies}
        />
        <ProtectedRoute
          path="/saved-movies"
          component={SavedMovies}
          onSearch={handleSearchSavedMovie}
          savedMovies={savedMovies}
          onSubmitCheckbox={handleSavedMoviesSubmitCheckbox}
          disabled={checkboxStatus}
          onDeleteMovie={handleDeleteMovie}
          onSaveMovie={handleSaveMovie}
          disabledCheckboxSaved={disabledCheckboxSaved}
          preloaderStatus={preloaderStatus}
        />
        <Route exact path="/signup">
        </Route>
        <Route exact path="/signin">
        </Route>
        <ProtectedRoute
          path="/profile"
          component={Profile}
        />
        <Route path="*" component={Page404} />
      </Switch>
      <Route exact path={footerEndpoints} component={Footer} />
    </div>
  );
}

export default App;
